import React, {useEffect, useState} from 'react';
import './App.css';
import Recipe from './Recipe';

const App = () =>{

  const APP_ID = "9a54d5f2";
  const APP_KEY = "65d1e6016ac21128afe777ed4c4c6df7";

  const [recipes, setRecipes]= useState([]);
  const [search, setSearch] = useState('');

useEffect ( () =>{
  getRecipes();
},[]);

const getRecipes = async () =>{
  const response = await fetch(`https://api.edamam.com/search?q=chicken&app_id=${APP_ID }&app_key=${APP_KEY}`);
  // &from=0&to=3&calories=591-722&health=alcohol-free
  const data = await response.json();
 setRecipes(data.hits);
 console.log(data.hits);
}

const updateSearch = e =>{
  setSearch(e.target.value);
  console.log(search);
}

  return (
    <div className="App">
      <form className="seach-form">
        <input className="search-bar" type="text" value ={search} onChange={updateSearch}/>
        <button className="search-button" type="submit">Search</button>

      </form>
 {recipes.map(recipe =>(
   <Recipe 
   key ={recipe.recipe.label}
   title={recipe.recipe.label} calories ={recipe.recipe.calories} image ={recipe.recipe.image}/>
 ))}
    </div>
  )
}

export default App;
